'use client';
import { useEffect, useState } from 'react';
import { getUserProgress, getLeaderboardData, checkDailyLogin, verifyUser, isUserVerified } from '@/utils/UserProgress';
import ProgressDashboard from '@/components/ProgressDashboard';
import Leaderboard from '@/components/Leaderboard';
import Link from 'next/link';

export default function Dashboard() {
    const [userProgress, setUserProgress] = useState(null);
    const [leaderboard, setLeaderboard] = useState([]);
    const [isVerified, setIsVerified] = useState(false);
    const [showConfetti, setShowConfetti] = useState(false);

    useEffect(() => {
        // Load initial data
        setUserProgress(getUserProgress());
        setLeaderboard(getLeaderboardData());
        setIsVerified(isUserVerified());

        // Check for daily login
        const loginReward = checkDailyLogin();
        if (loginReward) {
            // In a real app, show a modal or toast here
            console.log('Daily login reward:', loginReward);
            // Refresh progress
            setUserProgress(getUserProgress());
        }
    }, []);

    const handleVerify = () => {
        if (verifyUser()) {
            setIsVerified(true);
            setShowConfetti(true);
            setTimeout(() => setShowConfetti(false), 5000);
        }
    };

    const quickActions = [
        { title: 'Resume Learning', icon: 'Γû╢∩╕Å', link: '/course', color: 'from-blue-500 to-cyan-500' },
        { title: 'Take a Quiz', icon: '≡ƒô¥', link: '/quiz', color: 'from-purple-500 to-pink-500' },
        { title: 'Update Roadmap', icon: '≡ƒù║∩╕Å', link: '/roadmap', color: 'from-orange-500 to-red-500' },
        { title: 'Mock Interview', icon: '≡ƒÄÖ∩╕Å', link: '/interview', color: 'from-emerald-500 to-teal-500' }
    ];

    const momentumTips = [
        "≡ƒöÑ You're on a 3-day streak! Keep it up to earn a badge.",
        "≡ƒÆí Try a 'System Design' quiz to boost your backend skills.",
        "≡ƒæÇ 5 recruiters viewed your profile this week."
    ];

    return (
        <main className="min-h-screen pt-24 pb-12 px-4 sm:px-8 relative overflow-hidden bg-[var(--bg-dark)]">
            {/* Confetti Animation (Simple CSS implementation) */}
            {showConfetti && (
                <div className="fixed inset-0 pointer-events-none z-50 flex items-center justify-center">
                    <div className="text-6xl animate-bounce">≡ƒÄë</div>
                </div>
            )}

            {/* Dynamic Background */}
            <div className="fixed inset-0 w-full h-full overflow-hidden -z-10 pointer-events-none">
                <div className="absolute top-[-10%] right-[-10%] w-[600px] h-[600px] bg-[var(--primary)] rounded-full blur-[150px] opacity-10 animate-pulse-slow"></div>
                <div className="absolute bottom-[-10%] left-[-10%] w-[600px] h-[600px] bg-[var(--secondary)] rounded-full blur-[150px] opacity-10 animate-pulse-slow delay-1000"></div>
            </div>

            <div className="max-w-7xl mx-auto grid grid-cols-1 lg:grid-cols-3 gap-8">
                {/* Left Column: Progress & Stats */}
                <div className="lg:col-span-2 space-y-8">
                    <div className="animate-fade-in">
                        <h1 className="text-3xl font-bold text-white mb-2">Welcome back, Builder ≡ƒæï</h1>
                        <p className="text-[var(--text-muted)]">Here's your progress overview for today.</p>
                    </div>

                    {userProgress && <ProgressDashboard progress={userProgress} />}

                    {/* Quick Actions Grid */}
                    <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
                        {quickActions.map((action) => (
                            <Link
                                key={action.title}
                                href={action.link}
                                className="glass-panel p-6 rounded-2xl border border-white/5 hover:border-white/20 transition-all hover:-translate-y-1 group relative overflow-hidden"
                            >
                                <div className={`absolute inset-0 bg-gradient-to-br ${action.color} opacity-0 group-hover:opacity-10 transition-opacity duration-500`}></div>
                                <div className="flex items-center gap-4 relative z-10">
                                    <div className="w-12 h-12 rounded-xl bg-white/5 flex items-center justify-center text-2xl group-hover:scale-110 transition-transform">
                                        {action.icon}
                                    </div>
                                    <div>
                                        <h3 className="font-bold text-white">{action.title}</h3>
                                        <p className="text-xs text-[var(--text-muted)]">Continue where you left off</p>
                                    </div>
                                </div>
                            </Link>
                        ))}
                    </div>

                    {/* Capstone Project / Verification Card */}
                    <div className="glass-panel p-8 rounded-3xl border border-white/10 relative overflow-hidden">
                        <div className="absolute inset-0 bg-gradient-to-r from-blue-600/10 to-purple-600/10"></div>
                        <div className="relative z-10 flex flex-col md:flex-row items-center justify-between gap-6">
                            <div>
                                <h3 className="text-2xl font-bold text-white mb-2 flex items-center gap-2">
                                    {isVerified ? 'Γ£à AIcademy Verified' : '≡ƒÄô Capstone Project'}
                                </h3>
                                <p className="text-[var(--text-muted)] max-w-md">
                                    {isVerified
                                        ? "You have successfully verified your skills. Your profile is now highlighted to recruiters."
                                        : "Complete a final project to earn the 'AIcademy Verified' badge and boost your visibility."}
                                </p>
                            </div>
                            {!isVerified ? (
                                <button
                                    onClick={handleVerify}
                                    className="px-6 py-3 bg-white text-black font-bold rounded-xl hover:scale-105 transition-transform shadow-lg shadow-white/10 whitespace-nowrap"
                                >
                                    Submit Project
                                </button>
                            ) : (
                                <div className="px-6 py-3 bg-green-500/20 text-green-400 border border-green-500/30 font-bold rounded-xl flex items-center gap-2">
                                    <span>Verified</span>
                                </div>
                            )}
                        </div>
                    </div>
                </div>

                {/* Right Column: Leaderboard & Tips */}
                <div className="space-y-8">
                    <Leaderboard leaderboard={leaderboard} currentUserXP={userProgress?.xp || 0} />

                    <div className="glass-panel p-6 rounded-3xl border border-white/5">
                        <h3 className="font-bold text-white mb-4 flex items-center gap-2">
                            <span>≡ƒÜÇ</span> Momentum Tips
                        </h3>
                        <div className="space-y-4">
                            {momentumTips.map((tip, i) => (
                                <div key={i} className="p-4 rounded-xl bg-white/5 border border-white/5 text-sm text-[var(--text-muted)]">
                                    {tip}
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            </div>
        </main>
    );
}

'use client';

import { useState } from 'react';
import { signIn } from 'next-auth/react';
import { useRouter } from 'next/navigation';
import Link from 'next/link';

export default function LoginPage() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const router = useRouter();

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');

        try {
            const result = await signIn('credentials', {
                email,
                password,
                redirect: false,
            });

            if (result.error) {
                setError('Invalid credentials');
            } else {
                router.push('/dashboard');
                router.refresh();
            }
        } catch (err) {
            setError('Something went wrong');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="min-h-screen flex items-center justify-center bg-[var(--background)] px-4">
            <div className="glass-panel p-8 rounded-2xl w-full max-w-md border border-white/10">
                <div className="text-center mb-8">
                    <h1 className="text-3xl font-bold text-white mb-2">Welcome Back</h1>
                    <p className="text-[var(--text-muted)]">Sign in to continue your progress</p>
                </div>

                {error && (
                    <div className="bg-red-500/10 border border-red-500/50 text-red-500 p-3 rounded-lg mb-6 text-sm text-center">
                        {error}
                    </div>
                )}

                <form onSubmit={handleSubmit} className="space-y-4">
                    <div>
                        <label className="block text-sm font-medium text-[var(--text-secondary)] mb-1">Email</label>
                        <input
                            type="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            className="w-full bg-white/5 border border-white/10 rounded-lg px-4 py-3 text-white focus:outline-none focus:border-[var(--primary)] transition-colors"
                            placeholder="you@example.com"
                            required
                        />
                    </div>
                    <div>
                        <label className="block text-sm font-medium text-[var(--text-secondary)] mb-1">Password</label>
                        <input
                            type="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            className="w-full bg-white/5 border border-white/10 rounded-lg px-4 py-3 text-white focus:outline-none focus:border-[var(--primary)] transition-colors"
                            placeholder="ΓÇóΓÇóΓÇóΓÇóΓÇóΓÇóΓÇóΓÇó"
                            required
                        />
                    </div>

                    <button
                        type="submit"
                        disabled={loading}
                        className="w-full bg-gradient-to-r from-[var(--primary)] to-[var(--accent)] text-white font-bold py-3 rounded-lg hover:opacity-90 transition-opacity disabled:opacity-50"
                    >
                        {loading ? 'Signing in...' : 'Sign In'}
                    </button>
                </form>

                <div className="my-6 flex items-center">
                    <div className="flex-grow border-t border-white/10"></div>
                    <span className="mx-4 text-sm text-[var(--text-muted)]">OR</span>
                    <div className="flex-grow border-t border-white/10"></div>
                </div>

                <button
                    onClick={() => signIn("google", { callbackUrl: "/dashboard" })}
                    className="w-full bg-white text-black font-bold py-3 rounded-lg hover:bg-gray-200 transition-colors flex items-center justify-center gap-2"
                >
                    <svg className="w-5 h-5" viewBox="0 0 24 24">
                        <path
                            fill="currentColor"
                            d="M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92c-.26 1.37-1.04 2.53-2.21 3.31v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.09z"
                        />
                        <path
                            fill="currentColor"
                            d="M12 23c2.97 0 5.46-.98 7.28-2.66l-3.57-2.77c-.98.66-2.23 1.06-3.71 1.06-2.86 0-5.29-1.93-6.16-4.53H2.18v2.84C3.99 20.53 7.7 23 12 23z"
                        />
                        <path
                            fill="currentColor"
                            d="M5.84 14.09c-.22-.66-.35-1.36-.35-2.09s.13-1.43.35-2.09V7.07H2.18C1.43 8.55 1 10.22 1 12s.43 3.45 1.18 4.93l2.85-2.22.81-.62z"
                        />
                        <path
                            fill="currentColor"
                            d="M12 5.38c1.62 0 3.06.56 4.21 1.64l3.15-3.15C17.45 2.09 14.97 1 12 1 7.7 1 3.99 3.47 2.18 7.07l3.66 2.84c.87-2.6 3.3-4.53 6.16-4.53z"
                        />
                    </svg>
                    Continue with Google
                </button>

                <div className="mt-6 text-center text-sm text-[var(--text-muted)]">
                    Don't have an account?{' '}
                    <Link href="/register" className="text-[var(--primary)] hover:underline">
                        Sign up
                    </Link>
                </div>
            </div>
        </div>
    );
}

'use client';
import { useState } from 'react';
import GoalInput from '@/components/GoalInput';
import Quiz from '@/components/Quiz';
import Link from 'next/link';

export default function QuizPage() {
    const [step, setStep] = useState('goal'); // goal, quiz, results
    const [userGoal, setUserGoal] = useState('');
    const [quizResults, setQuizResults] = useState(null);

    const handleGoalSubmit = (goal) => {
        setUserGoal(goal);
        setStep('quiz');
    };

    const handleQuizComplete = (results) => {
        setQuizResults(results);
        setStep('results');
    };

    const handleRetake = () => {
        setStep('quiz');
        setQuizResults(null);
    };

    return (
        <main className="min-h-screen flex flex-col items-center justify-center p-4 sm:p-8 relative overflow-hidden bg-[var(--bg-dark)]">
            {/* Background Ambience */}
            <div className="fixed inset-0 w-full h-full overflow-hidden -z-10 pointer-events-none">
                <div className="absolute top-[-10%] right-[-10%] w-[800px] h-[800px] bg-[var(--primary)] rounded-full blur-[180px] opacity-20 animate-pulse-slow"></div>
                <div className="absolute bottom-[-10%] left-[-10%] w-[800px] h-[800px] bg-[var(--accent)] rounded-full blur-[180px] opacity-20 animate-pulse-slow delay-1000"></div>
            </div>

            {step === 'goal' && (
                <div className="glass-panel p-8 sm:p-14 rounded-[2.5rem] max-w-3xl w-full border border-white/10 shadow-2xl animate-fade-in relative overflow-hidden">
                    <div className="absolute top-0 left-0 w-full h-1 bg-gradient-to-r from-[var(--primary)] via-[var(--accent)] to-[var(--secondary)]"></div>

                    <div className="text-center mb-12">
                        <div className="inline-flex items-center justify-center w-20 h-20 rounded-3xl bg-white/5 border border-white/10 mb-8 shadow-lg transform hover:scale-110 transition-transform duration-500 group">
                            <span className="text-4xl group-hover:rotate-12 transition-transform duration-300">≡ƒºá</span>
                        </div>
                        <h1 className="text-5xl sm:text-6xl font-black mb-6 tracking-tight text-white">
                            Test Your <span className="text-transparent bg-clip-text bg-gradient-to-r from-[var(--primary)] to-[var(--accent)]">Skills</span>
                        </h1>
                        <p className="text-xl text-[var(--text-muted)] max-w-xl mx-auto leading-relaxed">
                            Challenge yourself with AI-generated quizzes tailored to any topic. Get instant feedback and level up your knowledge.
                        </p>
                    </div>
                    <GoalInput
                        onSubmit={handleGoalSubmit}
                        label="What do you want to master?"
                        description='e.g., "React Hooks", "Python Basics", "System Design"'
                        placeholder="Enter a topic..."
                        buttonText="Start Challenge"
                    />
                    <div className="mt-8 text-center">
                        <Link href="/dashboard" className="text-[var(--text-muted)] hover:text-white transition-colors flex items-center justify-center gap-2">
                            <span>ΓåÉ</span> Back to Dashboard
                        </Link>
                    </div>
                </div>
            )}

            {step === 'quiz' && <Quiz goal={userGoal} onComplete={handleQuizComplete} />}

            {step === 'results' && quizResults && (
                <div className="glass-panel p-8 sm:p-12 rounded-[2.5rem] max-w-5xl w-full animate-fade-in border border-white/10 shadow-2xl relative overflow-hidden">
                    <div className="absolute top-0 left-0 w-full h-1 bg-gradient-to-r from-[var(--primary)] via-[var(--accent)] to-[var(--secondary)]"></div>

                    <div className="text-center mb-12">
                        <div className="text-6xl mb-4 animate-bounce-slow">≡ƒÄë</div>
                        <h2 className="text-4xl sm:text-5xl font-black text-white mb-3">Quiz Complete!</h2>
                        <p className="text-xl text-[var(--text-muted)]">Here's how you performed</p>
                    </div>

                    {/* Score Card Grid */}
                    <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-12">
                        {/* Score */}
                        <div className="glass-card p-8 rounded-3xl text-center border border-white/5 relative overflow-hidden group hover:bg-white/5 transition-all">
                            <div className="absolute inset-0 bg-gradient-to-br from-[var(--primary)]/20 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-500"></div>
                            <p className="text-xs text-[var(--text-muted)] uppercase tracking-[0.2em] font-bold mb-3">Score</p>
                            <p className="text-6xl font-black text-white">
                                {quizResults.score}<span className="text-3xl text-[var(--text-muted)]">/{quizResults.total}</span>
                            </p>
                        </div>

                        {/* Accuracy */}
                        <div className="glass-card p-8 rounded-3xl text-center border border-white/5 relative overflow-hidden group hover:bg-white/5 transition-all">
                            <div className="absolute inset-0 bg-gradient-to-br from-[var(--accent)]/20 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-500"></div>
                            <p className="text-xs text-[var(--text-muted)] uppercase tracking-[0.2em] font-bold mb-3">Accuracy</p>
                            <p className="text-6xl font-black text-[var(--accent)]">
                                {Math.round((quizResults.score / quizResults.total) * 100)}<span className="text-3xl">%</span>
                            </p>
                        </div>

                        {/* Level */}
                        <div className="glass-card p-8 rounded-3xl text-center border border-white/5 relative overflow-hidden group hover:bg-white/5 transition-all">
                            <div className="absolute inset-0 bg-gradient-to-br from-[var(--secondary)]/20 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-500"></div>
                            <p className="text-xs text-[var(--text-muted)] uppercase tracking-[0.2em] font-bold mb-3">Level</p>
                            <p className="text-4xl font-black text-white mt-2">
                                {quizResults.level}
                            </p>
                        </div>
                    </div>

                    {/* Question Review */}
                    <div className="space-y-6 mb-12">
                        <h3 className="text-2xl font-bold text-white flex items-center gap-3 mb-6">
                            <span className="w-8 h-8 rounded-lg bg-white/10 flex items-center justify-center text-sm">≡ƒôï</span>
                            Detailed Review
                        </h3>
                        {quizResults.details.map((item, index) => (
                            <div
                                key={index}
                                className={`p-6 rounded-3xl border transition-all hover:shadow-lg ${item.isCorrect
                                    ? 'border-emerald-500/20 bg-emerald-500/5 hover:bg-emerald-500/10'
                                    : 'border-red-500/20 bg-red-500/5 hover:bg-red-500/10'
                                    }`}
                            >
                                <div className="flex items-start gap-5">
                                    <div className={`w-12 h-12 rounded-2xl flex items-center justify-center text-2xl shrink-0 ${item.isCorrect ? 'bg-emerald-500/20 text-emerald-400' : 'bg-red-500/20 text-red-400'
                                        }`}>
                                        {item.isCorrect ? 'Γ£ô' : 'Γ£ò'}
                                    </div>
                                    <div className="flex-1">
                                        <p className="font-bold text-xl text-white mb-4 leading-snug">
                                            <span className="text-[var(--text-muted)] mr-3 text-base font-normal">Q{index + 1}</span>
                                            {item.question}
                                        </p>

                                        <div className="grid md:grid-cols-2 gap-4 mb-5">
                                            {!item.isCorrect && (
                                                <div className="p-4 rounded-2xl bg-red-500/10 border border-red-500/20">
                                                    <p className="text-xs text-red-300 uppercase font-bold mb-2 tracking-wider">Your Answer</p>
                                                    <p className="text-red-100 font-medium text-lg">{item.userAnswer}</p>
                                                </div>
                                            )}
                                            <div className="p-4 rounded-2xl bg-emerald-500/10 border border-emerald-500/20">
                                                <p className="text-xs text-emerald-300 uppercase font-bold mb-2 tracking-wider">Correct Answer</p>
                                                <p className="text-emerald-100 font-medium text-lg">{item.correctAnswer}</p>
                                            </div>
                                        </div>

                                        <div className="p-5 rounded-2xl bg-white/5 border border-white/5">
                                            <p className="text-base leading-relaxed text-[var(--text-muted)]">
                                                <span className="font-bold text-[var(--primary)] mr-2">≡ƒÆí Insight:</span>
                                                {item.explanation || "Understanding this concept is key to mastering the topic."}
                                            </p>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>

                    {/* Actions */}
                    <div className="flex flex-col sm:flex-row gap-4 justify-center">
                        <button
                            onClick={handleRetake}
                            className="px-8 py-4 rounded-2xl font-bold glass-card hover:bg-white/10 hover:scale-[1.02] transition-all flex items-center justify-center gap-2 text-white"
                        >
                            <span>≡ƒöä</span> Retake Quiz
                        </button>
                        <Link
                            href="/dashboard"
                            className="px-8 py-4 rounded-2xl font-bold bg-gradient-to-r from-[var(--primary)] to-[var(--accent)] text-white hover:shadow-[0_0_30px_rgba(124,58,237,0.4)] hover:scale-[1.02] transition-all flex items-center justify-center gap-2"
                        >
                            <span>≡ƒôè</span> Back to Dashboard
                        </Link>
                    </div>
                </div>
            )}
        </main>
    );
}

